// Minimal stub of html2canvas for development without external dependency.
// If the full library is later added, this stub will be overridden.
(function(){
  function stubHtml2Canvas(element, options){
    return new Promise((resolve)=>{
      // Create a transparent 1Ã—1 canvas as placeholder
      const canvas=document.createElement('canvas');
      canvas.width=options && options.width || 1;
      canvas.height=options && options.height || 1;
      resolve(canvas);
    });
  }
  stubHtml2Canvas.version='stub';
  window.html2canvas=stubHtml2Canvas;
})();
